class DataBean {
  final int? id;
  final String? name;
  final bool? isOpenNow;
  final int? offersCount;
  final StatsBean? stats;
  final User_reactionsBean? userReactions;
  final String? shareUrl;
  final List<Opening_hoursBean>? openingHours;
  final ContactsBean? contacts;
  final SettingsBean? settings;
  final Main_imageBean? mainImage;
  final List<ImagesBean>? images;
  final LocationBean? location;
  final List<ServicesBean>? services;
  final List<OffersBean>? offers;

  DataBean(
      this.id,
      this.name,
      this.isOpenNow,
      this.offersCount,
      this.stats,
      this.userReactions,
      this.shareUrl,
      this.openingHours,
      this.contacts,
      this.settings,
      this.mainImage,
      this.images,
      this.location,
      this.services,
      this.offers);

  static DataBean fromMap(Map<String, dynamic> map) {
    DataBean dataBean = DataBean(
      map['id'],
      map['name'],
      map['is_open_now'],
      map['offers_count'],
      map['stats'] != null ? StatsBean.fromMap(map['stats']) : null,
      map['user_reactions'] != null
          ? User_reactionsBean.fromMap(map['user_reactions'])
          : null,
      map['share_url'],
      map['opening_hours'] != null
          ? ([]..addAll((map['opening_hours'] as List)
              .map((o) => Opening_hoursBean.fromMap(o))))
          : null,
      map['contacts'] != null ? ContactsBean.fromMap(map['contacts']) : null,
      map['settings'] != null ? SettingsBean.fromMap(map['settings']) : null,
      map['main_image'] != null
          ? Main_imageBean.fromMap(map['main_image'])
          : null,
      map['images'] != null
          ? ([]
            ..addAll((map['images'] as List).map((o) => ImagesBean.fromMap(o))))
          : null,
      map['location'] != null ? LocationBean.fromMap(map['location']) : null,
      map['services'] != null
          ? ([]..addAll(
              (map['services'] as List).map((o) => ServicesBean.fromMap(o))))
          : null,
      map['offers'] != null
          ? ([]
            ..addAll((map['offers'] as List).map((o) => OffersBean.fromMap(o))))
          : null,
    );
    return dataBean;
  }

  Map toJson() => {
        "id": id,
        "name": name,
        "is_open_now": isOpenNow,
        "offers_count": offersCount,
        "stats": stats?.toJson(),
        "user_reactions": userReactions?.toJson(),
        "share_url": shareUrl,
        "opening_hours":
            openingHours?.map((o) => o.toJson()).toList(growable: false),
        "contacts": contacts?.toJson(),
        "settings": settings?.toJson(),
        "main_image": mainImage?.toJson(),
        "images": images?.map((o) => o.toJson()).toList(growable: false),
        "location": location?.toJson(),
        "services": services?.map((o) => o.toJson()).toList(growable: false),
        "offers": offers?.map((o) => o.toJson()).toList(growable: false),
      }..removeWhere((k, v) => v == null);
}

